mapper.select:
SELECT shf.line_code, shf.start_station_id, shf.end_station_id, s1.station_name as start_station_name, s2.station_name as end_station_name, shf.flow_date, shf.hour, CONCAT(LPAD(shf.hour, 2, '0'), ':00-', LPAD(shf.hour, 2, '0'), ':59') as time_range, shf.passenger_count, shf.train_capacity, shf.load_rate FROM section_hourly_flow shf LEFT JOIN station s1 ON shf.start_station_id = s1.site_id LEFT JOIN station s2 ON shf.end_station_id = s2.site_id WHERE shf.is_deleted = 0
AND shf.line_code = #{query.lineCode}
AND shf.flow_date = #{query.flowDate}
AND shf.hour >= #{query.startHour}
AND shf.hour <= #{query.endHour}
ORDER BY shf.line_code, shf.start_station_id, shf.hour

SELECT * FROM section_hourly_flow
WHERE is_deleted = 0

    AND line_code = #{lineCode}


    AND flow_date = #{flowDate}

ORDER BY line_code, start_station_id, hour

SELECT * FROM section_hourly_flow
WHERE line_code = #{lineCode}
          AND start_station_id = #{startStationId}
          AND end_station_id = #{endStationId}
          AND flow_date = #{flowDate}
          AND hour = #{hour}
  AND is_deleted = 0

SELECT * FROM section_daily_flow
WHERE line_code = #{lineCode}
  AND start_station_id = #{startStationId}
  AND end_station_id = #{endStationId}
  AND flow_date = #{flowDate}
  AND is_deleted = 0

mapper.update:
UPDATE section_daily_flow
SET avg_load_rate = #{avgLoadRate},
    max_load_rate = #{maxLoadRate},
    update_time = NOW(),
    update_by = #{updateBy}
WHERE id = #{id}

mapper.select:
SELECT * FROM overload_alert
WHERE line_code = #{lineCode}
  AND start_station_id = #{startStationId}
  AND end_station_id = #{endStationId}
  AND alert_start_date = #{alertStartDate}
  AND alert_end_date = #{alertEndDate}
  AND is_deleted = 0

SELECT
hour,
AVG(load_rate) as avg_load_rate
FROM section_hourly_flow
WHERE line_code = #{lineCode}
AND start_station_id = #{startStationId}
AND end_station_id = #{endStationId}
AND flow_date BETWEEN #{startDate} AND #{endDate}
AND is_deleted = 0
GROUP BY hour
HAVING AVG(load_rate) > 80
ORDER BY avg_load_rate DESC
LIMIT 5

mapper.delete:
UPDATE section_hourly_flow SET is_deleted = 1, update_time = NOW() WHERE flow_date < DATE_SUB(CURDATE(), INTERVAL 30 DAY) AND is_deleted = 0;
UPDATE section_daily_flow SET is_deleted = 1, update_time = NOW() WHERE flow_date < DATE_SUB(CURDATE(), INTERVAL 90 DAY) AND is_deleted = 0;
UPDATE overload_alert SET status = 'RESOLVED', update_time = NOW() WHERE alert_end_date < DATE_SUB(CURDATE(), INTERVAL 7 DAY) AND status = 'ACTIVE';

mapper.select:
SELECT hspc.operation_line_code as line_code, hspc.depart_station_id as start_station_id, hspc.arrive_station_id as end_station_id, s1.station_name as start_station_name, s2.station_name as end_station_name, hspc.travel_date as flow_date, HOUR(hspc.depart_time) as hour, COALESCE(SUM(hspc.boarding_count), 0) as passenger_count, t.train_capacity as train_capacity, CASE WHEN t.train_capacity > 0 THEN (SUM(hspc.boarding_count) * 100.0 / t.train_capacity) ELSE 0 END as load_rate, t.train_code as train_code, t.train_number as train_type, t.train_id as train_number, SUM(hspc.boarding_count) as boarding_count, SUM(hspc.alighting_count) as alighting_count FROM high_speed_passenger_clean hspc LEFT JOIN station s1 ON hspc.depart_station_id = s1.site_id LEFT JOIN station s2 ON hspc.arrive_station_id = s2.site_id LEFT JOIN train t ON hspc.train_code = t.train_code WHERE hspc.is_deleted = 0 AND t.is_deleted = 0
AND hspc.operation_line_code = #{query.lineCode}
AND hspc.travel_date = #{query.flowDate}
AND HOUR(hspc.depart_time) >= #{query.startHour}
AND HOUR(hspc.depart_time) <= #{query.endHour}
AND hspc.depart_station_id = #{query.startStationId}
AND hspc.arrive_station_id = #{query.endStationId}
AND s1.station_name = #{query.startStationName}
AND s2.station_name = #{query.endStationName}
GROUP BY hspc.operation_line_code, hspc.depart_station_id, hspc.arrive_station_id, hspc.travel_date, HOUR(hspc.depart_time), t.train_capacity, t.train_code, t.train_number, t.train_id, s1.station_name, s2.station_name HAVING passenger_count > 0 ORDER BY hspc.operation_line_code, hspc.depart_station_id, HOUR(hspc.depart_time)

SELECT hspc.operation_line_code as line_code, hspc.depart_station_id as start_station_id, hspc.arrive_station_id as end_station_id, s1.station_name as start_station_name, s2.station_name as end_station_name, hspc.travel_date as flow_date, HOUR(hspc.depart_time) as hour, COALESCE(SUM(hspc.boarding_count), 0) as passenger_count, t.train_capacity as train_capacity, CASE WHEN t.train_capacity > 0 THEN (SUM(hspc.boarding_count) * 100.0 / t.train_capacity) ELSE 0 END as load_rate, t.train_code as train_code, t.train_number as train_type, t.train_id as train_number, SUM(hspc.boarding_count) as boarding_count, SUM(hspc.alighting_count) as alighting_count FROM high_speed_passenger_clean hspc LEFT JOIN station s1 ON hspc.depart_station_id = s1.site_id LEFT JOIN station s2 ON hspc.arrive_station_id = s2.site_id LEFT JOIN train t ON hspc.train_code = t.train_code WHERE hspc.is_deleted = 0 AND t.is_deleted = 0
AND hspc.operation_line_code = #{params.query.lineCode}
AND hspc.travel_date = #{params.query.flowDate}
AND HOUR(hspc.depart_time) >= #{params.query.startHour}
AND HOUR(hspc.depart_time) <= #{params.query.endHour}
AND hspc.depart_station_id = #{params.query.startStationId}
AND hspc.arrive_station_id = #{params.query.endStationId}
AND s1.station_name = #{params.query.startStationName}
AND s2.station_name = #{params.query.endStationName}
GROUP BY hspc.operation_line_code, hspc.depart_station_id, hspc.arrive_station_id, hspc.travel_date, HOUR(hspc.depart_time), t.train_capacity, t.train_code, t.train_number, t.train_id, s1.station_name, s2.station_name HAVING passenger_count > 0 ORDER BY hspc.operation_line_code, hspc.depart_station_id, HOUR(hspc.depart_time) LIMIT #{params.query.size} OFFSET #{params.offset}

SELECT COUNT(*) FROM ( SELECT hspc.operation_line_code, hspc.depart_station_id, hspc.arrive_station_id, hspc.travel_date, HOUR(hspc.depart_time) FROM high_speed_passenger_clean hspc LEFT JOIN station s1 ON hspc.depart_station_id = s1.site_id LEFT JOIN station s2 ON hspc.arrive_station_id = s2.site_id LEFT JOIN train t ON hspc.train_code = t.train_code WHERE hspc.is_deleted = 0 AND t.is_deleted = 0
AND hspc.operation_line_code = #{query.lineCode}
AND hspc.travel_date = #{query.flowDate}
AND HOUR(hspc.depart_time) >= #{query.startHour}
AND HOUR(hspc.depart_time) <= #{query.endHour}
AND hspc.depart_station_id = #{query.startStationId}
AND hspc.arrive_station_id = #{query.endStationId}
AND s1.station_name = #{query.startStationName}
AND s2.station_name = #{query.endStationName}
GROUP BY hspc.operation_line_code, hspc.depart_station_id, hspc.arrive_station_id, hspc.travel_date, HOUR(hspc.depart_time), t.train_capacity, t.train_code, t.train_number, t.train_id, s1.station_name, s2.station_name HAVING COALESCE(SUM(hspc.boarding_count), 0) > 0 ) AS total_count
